func getEthBalance(address common.Address, client *ethclient.Client) (*big.Int, *big.Float) {
	balance, err := client.BalanceAt(context.Background(), address, nil)
	if err != nil {
		log.Fatal(err)
	}
	fbalance := new(big.Float)
	fbalance.SetString(balance.String())
	ethBalance := new(big.Float).Quo(fbalance, big.NewFloat(math.Pow10(18)))
	return balance, ethBalance
}

func getWethBalance(address common.Address, client *ethclient.Client) (*big.Int, *big.Float) {
	wethBalanceContractAddress := common.HexToAddress("0xc02aaa39b223fe8d0a0e5c4f27ead9083c756cc2")
	parsedABI, err := abi.JSON(strings.NewReader(wethBalanceContractABI))
	if err != nil {
		log.Fatal(err)
	}
	contract := bind.NewBoundContract(wethBalanceContractAddress, parsedABI, client, client, client)
	var balance *big.Int
	err = contract.Call(nil, &[]interface{}{&balance}, "balanceOf", address)
	if err != nil {
		log.Fatalf("Error while calling balanceOf", err)
	}
	var decimals uint8
	err = contract.Call(nil, &[]interface{}{&decimals}, "decimals")
	if err != nil {
		log.Fatalf("Error while calling decimals:", err)
	}
	fbalance := new(big.Float)
	fbalance.SetString(balance.String())
	wethBalance := new(big.Float).Quo(fbalance, big.NewFloat(math.Pow10(int(decimals))))
	return balance, wethBalance
}

func EthWethBalance(address common.Address, client *ethclient.Client) (*big.Int, *big.Float, *big.Int, *big.Float, *big.Float, *big.Float) {
	balance, ethBalance := getEthBalance(address, client)

	erc20balance, wethBalance := getWethBalance(address, client)

	chainlinkAddress := common.HexToAddress("0x5f4eC3Df9cbd43714FE2740f5E3616155c5b8419")
	parsedABI, err := abi.JSON(strings.NewReader(chainlinkContractABI))
	if err != nil {
		log.Fatal(err)
	}
	contract := bind.NewBoundContract(chainlinkAddress, parsedABI, client, client, client)
	var answer [5]*big.Int
	err = contract.Call(nil, &[]interface{}{&answer}, "latestRoundData")
	if err != nil {
		log.Fatalf("Error while calling latestRoundData", err)
	}

	var decimals uint8
	err = contract.Call(nil, &[]interface{}{&decimals}, "decimals")
	if err != nil {
		log.Fatalf("Error while calling decimals:", err)
	}
	exchangeRate := new(big.Float).Quo(new(big.Float).SetInt(answer[1]), new(big.Float).SetInt(new(big.Int).Exp(big.NewInt(10), big.NewInt(int64(decimals)), nil)))
	product := new(big.Int).Mul(new(big.Int).Add(balance, erc20balance), answer[1])
	divisor := new(big.Int).Exp(big.NewInt(10), big.NewInt(int64(18+decimals)), nil)
	usdBalance := new(big.Float).Quo(new(big.Float).SetInt(product), new(big.Float).SetInt(divisor))
	return balance, ethBalance, erc20balance, wethBalance, exchangeRate, usdBalance
}
